import { Web3ReactProvider, useWeb3React, UnsupportedChainIdError } from '@web3-react/core'
import ChainId from './ChainId'
import Account from './Account'
import Balance from './Balance'
import { AppBar, Badge } from '@material-ui/core'
import AccountBalanceWalletIcon from '@material-ui/icons/AccountBalanceWallet';
import { makeStyles } from '@material-ui/core/styles';


export default function Header() {
  const { active, error } = useWeb3React()
  
const useStyle = makeStyles((theme) => ({
		
		root: {
			
			[theme.breakpoints.only('xs')]:{
				
				display: 'flex',
				flexDirection: 'row',
				width: '100%'
				
			},
			
			[theme.breakpoints.between('sm', 'xl')]:{
				
				width: '50vw',
				display: 'flex',
				flexDirection: 'column'
				
			}
			
		}
		
	}));

	const classes = useStyle();	

  return (
    <>
    <AppBar className={classes.root}	>
	  <h1 style={{ margin: '1rem', textAlign: 'right' }}>{active ? <AccountBalanceWalletIcon fontSize='large' /> : error ? <AccountBalanceWalletIcon fontSize='large' /> : <AccountBalanceWalletIcon fontSize='large' />}</h1>
      <h3
        style={{
          display: 'grid',
          gridGap: '1rem',
          gridTemplateColumns: '1fr min-content 1fr',
          maxWidth: '20rem',
          lineHeight: '2rem',
          margin: 'auto'
        }}
		>
        <ChainId />
        <Account />
        <Balance />
      </h3>
	 </AppBar> 
    </>
  )
}
